export const AddItemToCart =(cartItems,cartItemtoAdd)=>{
    const existingcartitem = cartItems.find(cartItem=>
        cartItem.id === cartItemtoAdd.id)

        if(existingcartitem){
            return cartItems.map((cartItem)=>
            cartItem.id === cartItemtoAdd.id
            ) ? {
                ...cartItem,
            quantity:cartItem.quantity+1
        } : cartItem
        }

        return[
            ...cartItems,
            {
            cartItemtoAdd,
            quantity:1
        }
        ]
}
